import React from 'react'
import useClock from '../lib/useClock';

export default function Clock() {
  const time = useClock(new Date())

  const dayObj = {
    1: '星期一',
    2: '星期二',
    3: '星期三',
    4: '星期四',
    5: '星期五',
    6: '星期六',
    0: '星期天'
  }

  return (
    <div className='main'>
      <div className='code'>
        <code>
          {`
          const useClock = (initTime) => {
          const [time, setTime] = useState(initTime)

          useEffect(() => {
            const id = setInterval(() => {
              setTime(new Date())
              }, 1000)
            return () => clearInterval(id)
            }, [])

        return time
          }
         `}
        </code>
      </div>
      <div className="result">
        <p>{(dayObj[time.getDay()] || '') + time.toLocaleTimeString()}</p>
      </div>
      <style jsx>
        {`
          .main {
            display: flex;
            /* flex-wrap: wrap; */
            margin-top: 5rem;
            left: 10vw;
            width: 80vw;
          }

          .code {
            width: 40vw;
          }

          .result {
            width: 40vw;
          }

        `}
      </style>
    </div>
  )
}
