import React from 'react';
import axios from 'axios';
import cheerio from 'cheerio';
import { useState } from 'react';

const Data: React.FC = () => {

  const getData = (url) => {
    axios.get(url).then(resp => {
      var $ = cheerio.load(resp.data)
      var lis = $("#grid_view li")
      var movies = []
      for (var i = 0; i < lis.length; i++) {
        var li = lis.eq(i)
        var movie = {
          name: li.find("[class=title]").text().trim() || '',
          img: li.find("img").attr("src") || '',
          info: li.find("[class=inq").text().trim() || ''
        }
        movies.push(movie)
      }
      console.log(movies)
    })
  }
  return (
    <button onClick={() => getData("https://movie.douban.com/top250")}>
      点击
    </button>
  )
}

export default Data
