import React from 'react'
import useClock from '../lib/useClock';

export default function Clock() {
  const time = useClock(new Date())

  const timeobj = {
    1: '星期一',
    2: '星期二',
    3: '星期三',
    4: '星期四',
    5: '星期五',
    6: '星期六',
    0: '星期天'
  }

  return(
    <div>
      <p>{ (timeobj[useClock(new Date()).getDay()] || '') + useClock(new Date()).toLocaleTimeString() }</p>
    </div>
  )
}
