import React from 'react';
import Head from 'next/head'
import { useState } from 'react';
import qrcode from '../public/images/qrcode.jpg'

const App: React.FC = () => {

  const fetcher = (url: string) =>
    fetch(url)
      .then(r => r.json())

  // const { data } = useSWR('http://localhost:7001/movie', fetcher)


  function Getmovie() {
    const i = Math.ceil(Math.random() * 250) - 1
    // setMovie(data[i])
  }

  const initState = {
    name: '肖申克的救赎',
    img: 'https://img3.doubanio.com/view/photo/s_ratio_poster/public/p480747492.webp',
    info: '希望让人自由'
  }
  const [movie, setMovie] = useState(initState)

  return (
    <div className='movie_main'>
      <Head>
        <title>高分电影</title>
        <meta name="keywords" content="电影,高分电影,豆瓣电影" />
        <meta name="description" content="每天看一部豆瓣高分电影" />
        <meta name="referrer" content="no-referrer"/>
      </Head>
      <img className='cover' alt={movie.name} src={movie.img} />
      <p>{'《' + movie.name + '》'}</p>
      <p>{movie.info || ''}</p>
      <button onClick={Getmovie}>
        换一部
      </button>
      <p>截屏分享</p>
      <img alt='截屏分享' height={40} width={40}
      // src={qr}
      />
      <style jsx>
        {`
        .cover {
	        width: 18.75rem;
	        height: calc(18.75rem * 1.5);
        }

        .movie_main {
          flex-direction: column;
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          align-items: center;
        }

        p {
          text-align: center;
        }

        button {
          display: block;
          width: 7.5rem;
          height: 2.5rem;
          background-color: #33ccff;
          color: #FFFFFF;
          text-align: center;
          font-size: 18px;
          line-height: 40px;
          border-radius: 25px;
          border: none;
          box-shadow: none;
          text-decoration: none;
          transition: box-shadow 0.5s;
          -webkit-transition: box-shadow 0.5s;
        }

        button:hover {
          box-shadow: 0px 0px 5px 1px #808080;
        }

        button:active {
          box-shadow: 0px 0px 5px 1px #FF0000;
        }`}
      </style>
    </div>
  )
}

export default App
